mapper.select:
 
		SELECT *
		FROM (SELECT CODE_TYPE, NUM, NAME, TITLE, CONTENT, TO_CHAR(REGDATE , 'YYYY/MM/DD') AS REGDATE,
		case when CODE_TYPE = '01' THEN '자유'
		when CODE_TYPE = '02' THEN '익명'
		ELSE 'qna' END AS codeType,ROW_NUMBER() OVER(ORDER BY NUM DESC) AS RNUM
		         FROM FREEBOARD )WHERE RNUM BETWEEN #{startrow} AND #{endrow}
		
   		AND CODE_TYPE = #{codeType} 
  		
  		
   		AND name = #{name}
  		
  		
   		AND  title = #{title}
  		
  		
   		 AND content = #{content} 
  		
  		
   		AND  NUM = #{num}
  		
  		
   		AND TO_CHAR(REGDATE,'YYYYMMDD')  BETWEEN #{regdate} AND #{regdate2}
  		
  	
  		order by NUM DESC

  		SELECT COUNT(NUM)
		  FROM FREEBOARD
		 WHERE NUM > 0
		
   		AND CODE_TYPE = #{codeType} 
  		
  		
   		AND name = #{name}
  		
  		
   		AND  title = #{title}
  		
  		
   		 AND content = #{content} 
  		
  		
   		AND  NUM = #{num}
  		
  		
   		AND TO_CHAR(REGDATE,'YYYYMMDD')  BETWEEN #{regdate} AND #{regdate2}

mapper.update:
		UPDATE FREEBOARD
		SET TITLE = #{title}
				, CONTENT = #{content}
		WHERE NUM = #{num}
